<form action="comp_LK_list.php" method="post" class="form-horizontal" >	<h3 align="center"> Список личных карточек сотрудников ПАО "КЭТЗ"	 		<?=$input;?>		<input type="hidden" id="sentOtdel" name="sentOtdel" value = "">		<input type="submit" class="btn btn-primary" name="submit" value="Раскрыть"> 	</h3> 	<table >		<tr>		<?=$one;?>		</tr>	</table></form>	<script>function f1(x) {	document.getElementById('sentOtdel').value = x;}//размер select	 setTimeout(function() {		 jQuery(function() {			jQuery('.chosen-select').chosen({width: '250px'});		 });	 });	 //выбор сотрудника через отдел$('.ajaxy_one'). btn.on('click', function() {    var ths = $(this);    $.ajax({      url: '/comp_LK_list.php', // куда идет запрос (текущий адрес)      type: 'GET', // по-умолчанию      dataType: 'html', // по-умолчанию      data: { // данные, которые передаем        ajax_one: '1', // по-умолчанию        one: $(this).attr('name'), // тип селекта (название поля)        id: $(this).val() // ид выбранного поля      },      success: function(data, textStatus, xhr) { // когда пришел ответ	  console.log(data);        if(data) { // ответ          ths // this - текущий селект			.closest('.control-group') // ближайщий элемент с классом .control-group			.next('.control-group') // следующий элемент			.find('input, select') // найти элементы input или select			.after(data) // вставить после data			.remove(); // удалить текущий элемент (input или select)        } else {			ths // this - текущий селект			.closest('.control-group') // ближайщий элемент с классом .control-group			.next('.control-group') // следующий элемент			.find('select').val('');		}      },      error: function(xhr, textStatus, errorThrown) {        alert('Небольшие проблемы, обновите страницу.');      }    });  }); </script>